import React from 'react'
import { useState } from 'react'
import './Hw1.css'

function Hw1() {
  const [arr, setArr] = useState("")
  function showText(event) {
    event.preventDefault()
    setArr((prev) => {return prev = document.getElementsByClassName("input").value});
    document.getElementsByClassName("screen").innerText = arr;
    console.log(document.getElementsByClassName("screen").innerText);
  }
  return (
    <>
      <form className='hw1'>
        <input className='input' type="text"/>
        <button onClick={showText}>OK</button>
      </form>
      <div className='screen'></div>
    </>
  )
}

export default Hw1
