import React from 'react';
import './Day8.css';

export default function Day8(props) {
    const [productsList, setProductList] = useState([])
    const API = async() => {
        const products = await (await fetch('https://fakestoreapi.com/products')).json()
        setProductList(products)
    }
    return (
        <>
            <button onClick={API}>Call API</button>
            {productsList !== [] ? productsList.map((item, index) => {
                <p key={index}>{item}</p>
            })  : <p>Loading</p>}
        </>
    )
}
