import React from 'react'
import { useState } from 'react'
import './Hw2.css'

function Hw2() {
  const arr = [0, 1, 2]
  const [num, setNum] = useState(0)
  const changeNum = () => {
    setNum((prev) => {return prev === 6 ? 0 : prev +1})
  }
  return (
    <form className='hw2'>
      <button type='submit' onClick={changeNum}>Add</button>
      <div className='list'>
        {
          arr.map((item, index) => (
            <div key={index} className='list-items'>{item}</div>
        ))}
      </div>
    </form>
  )
}

export default Hw2
