import React from 'react'
import { useState, useEffect } from 'react'
import './Hw.css'

function Hw() {
  const [showScrollbar, setShowScrollbar] = useState(false);
  const [scrollBarPosition, setScrollBarPosition] = useState(0)
  useEffect(() => {
    const handleScrolling = () => {
      setScrollBarPosition(document.documentElement.scrollTop)
      console.log(scrollBarPosition);
    }
    document.addEventListener('scroll', handleScrolling)
    return () => {
      document.removeEventListener('scroll', handleScrolling)
    }
  },[showScrollbar])
  return (
    <div className='traffic-lights'>
      <button onClick={() => setShowScrollbar(!showScrollbar)}>Toggle scrollbar</button>
      {showScrollbar && <div style={{height: 4000}}></div>}
    </div>
  )
}

export default Hw
