import React from 'react';
import { useState } from 'react';
import './Hw2.css';
import { v4 } from 'uuid';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Hw2() {
  const [todo, setToDo] = useState("");
  const [num, setNum] = useState([]);
  function addToDo(event) {
    event.preventDefault();
    if (num.includes(todo)) {
      alert("Không thể trùng lặp")
      return;
    }
    const item = {
      id: v4(),
      name: todo,

    }
    toast.success("Created")
    setNum([...num, item])
    setToDo("")
    console.log(num);
  }

  function removeToDo(event) {
    // const arr = []
    // for (let index = 0; index < num.length; index++) {
    //   const element = [...num][index];
    //   if (element.id !== event.target.value) {
    //     arr.push(element)
    //   }
    // }
    // setNum(arr)
    setNum(num.filter(a => a.id !== event.target.value))
    toast.alert('Removed')
  }
  return (
    <>
      <h1>Todo</h1>
      <form onSubmit={addToDo} className='hw2'>
        <input type="text" onChange={(e) => setToDo(e.target.value)} value={todo} placeholder='Add todo' />
        <button type='submit'>Add</button>
      </form>
      <div className='list'>
        {
          num.map((item, index) => (
            <div key={item.id} className='list-items'>
              <div>
                <input type="checkbox" />
                <div>{item.name}</div>
              </div>
              <button value={item.id} onClick={removeToDo}>Delete</button>
            </div>
          ))}
      </div>
      <ToastContainer theme='dark' />
    </>
  )
}

export default Hw2
