    event.preventDefault()
    // setArr(arr = event.target.value)
    console.log(event.target.value);
  }
  return (
    <>
      <form className='hw1'>
        <input type="text"/>
        <button onClick={showText}>OK</button>
      </form>
      <div>{arr}</div>
    </>
  )
}

export default Hw1
