import React from 'react'
import { useState } from 'react'
import './Hw2.css'

function Hw2() {
  const traffcLightsClassName = [0, 1, 2, 3, 4, 5, 6]
  const [trafficLights, setTrafficLights] = useState(0)
  const changeTrafficLights = () => {
    // setTrafficLights(trafficLights === 7 ? 1 : trafficLights + 1)
    setTrafficLights((prev) => {return prev === 6 ? 0 : prev +1})
  }
  return (
    <div className='traffic-lights'>
      <button onClick={changeTrafficLights}>Next</button>
      <div>
        {
          traffcLightsClassName.map((light, index) => (
            <div key={index} className={`light ${index === trafficLights && "active"}`}></div>
        ))}
      </div>
    </div>
  )
}

export default Hw2
